import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Button, Container } from "react-bootstrap";
import { useState } from 'react';
import './App.css';

function App() {
  const [keywords, setKeywords] = useState(1);

  const changeSlider = e => {
    setKeywords(e.target.value);
  };

  return (
    <div className="App">
      <Container>
        <Form>
          <Form.Group className="mb-3" controlId="formBasicText">
            <Form.Label>Text to analyze</Form.Label>
            <Form.Control as="textarea" placeholder="Enter text" rows="5" />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicRange">
            <Form.Label>Number of keywords: {keywords}</Form.Label>
            <Form.Range min={1} max={5} defaultValue={1} onChange={changeSlider}/>
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </Container>
    </div >
  );
}

export default App;
